    /* HELPERS */


    function _getdatebetweentwodates($startdate, $enddate, $interval = 'P1D', $format = 'Y-m-d'){
        $period = new DatePeriod(
            new DateTime($startdate),
            new DateInterval($interval),
            new DateTime($enddate)
        );

        $dates = '';

        if(sizeof($period)){
            foreach ($period as $value) {
                $dates[] = $value->format($format);
            }
        }

        return $dates;

    }

    function _frequency($freq)
    {
        switch ($freq) {
            case 'OD':
            $text = 'Once Daily';
            break;
            
            case 'TD/BD':
            $text = 'Twice Daily';
            break;

            case 'TDS/TID':
            $text = '3 Times Daily/As Dispended';
            break;

            case 'QDS':
            $text = '4 Times Daily';
            break;

            case 'NOCTE':
            $text = 'Night Time';
            break;

            case 'WEEKLY':
            $text = 'WEEKLY';
            break;

            case '24 HRLY':
            $text = '24 HRLY';
            break;

            case '6 HRLY':
            $text = '6 HRLY';
            break;

            case 'Interval':
            $text = 'Interval';
            break;
        }

        return $text;
    }
    
